char *jstrToChar(JNIEnv *env, jstring jstring1) {
    const char *ch = (*env)->GetStringUTFChars(env, jstring1, 0);
    (*env)->DeleteLocalRef(env,jstring1);
    LOGI("jstrToChar: %s", ch);
    return ch;
}

jstring charToJstr(JNIEnv *env, char *c) {
    LOGI("charToJstr : %s", c);
    char *buf = (char *) malloc(100);
    strcpy(buf, c);
    jstring jstrBuf = (*env)->NewStringUTF(env, buf);
    free(buf);
    free(c);
    return jstrBuf;
}

